array(  
'NAME' => "Элемент", - название  
'ACTIVE' => 'Y',  
'PROPERTY_VALUES'=> array( - список свойств  
'UF_DEAL' => 18029, - ИД сделки  
'UF_CONTACT' => 30694, ИД контакта  
'UF_NAME' => 'Иван Иванов', - Имя фамилия  
'UF_EMAIL' => 'sdfgh@gmail.com', - Email  
'UF_AGREEMENT_DATE' => '03.06.2021 23:56:55', - дата соглашения  
'UF_IP' => '192.168.10.11', - IP  
'UF_USER_AGENT' =>'test agent', - Агент  
'UF_AGREEMENT' =>90599, - ИД условия  
